from lego_product_repertory WHERE 1=1

  and product_id = #{productId,jdbcType=INTEGER}

limit #{from}, #{pageSize}

select count(1)
from lego_product_repertory WHERE 1=1

  and product_id = #{productId,jdbcType=INTEGER}
